use Oryzone\MediaStorage\Exception\InvalidContentException;

/**
 * Generated by PHPUnit_SkeletonGenerator 1.2.0 on 2013-01-06 at 10:15:54.
 */
class InvalidContentExceptionTest extends \PHPUnit_Framework_TestCase
{
    /**
     * @var InvalidContentException
     */
    protected $exception;

    protected $provider;

    protected $media;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp()
    {
        $this->provider = $this->getMock('\Oryzone\MediaStorage\Provider\ProviderInterface');
        $this->media = $this->getMock('\Oryzone\MediaStorage\Model\MediaInterface');
        $this->exception = new InvalidContentException('message', $this->provider, $this->media);
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown()
    {
    }

    public function testGetMedia()
    {
        $this->assertEquals($this->media, $this->exception->getMedia());
    }

    public function testGetProvider()
    {
        $this->assertEquals($this->provider, $this->exception->getProvider());
    }
}
